import { Component, Input, OnInit } from '@angular/core';
import { AuthService } from 'src/app/services/auth.service';
import { UserService } from 'src/app/services/user.service';

@Component({
  selector: 'app-home-owner',
  templateUrl: './home-owner.component.html',
  styleUrls: ['./home-owner.component.scss']
})
export class HomeOwnerComponent implements OnInit{

  @Input() user: any;
  restaurantOwner: any;

  constructor(
    private userService: UserService,
    public authService: AuthService){}

  ngOnInit(): void {
    this.user = localStorage.getItem('user');
    this.userService.getRestaurantOwner(this.user.restaurantOwner_Id).subscribe({
      next: (res)=>{
        this.restaurantOwner = res;
      },
      error: (err) =>{
        console.log(err);
      }
    })

  }

}
