import { Component, Input, OnInit } from '@angular/core';
import { AuthService } from 'src/app/services/auth.service';
import { UserService } from 'src/app/services/user.service';

@Component({
  selector: 'app-home-rider',
  templateUrl: './home-rider.component.html',
  styleUrls: ['./home-rider.component.scss']
})
export class HomeRiderComponent implements OnInit {

  rider: any;

  constructor(
    private userService: UserService,
    public authService: AuthService){}

  ngOnInit(): void {
    // this.userService.datiUtente.subscribe({
    //   next: (res)=>{
    //     this.user = res;
    //   },
    //   error: (err) => console.log(err)
    // })
    const user = JSON.parse(localStorage.getItem('user'));

    this.userService.getRider(this.user.rider_Id).subscribe({
      next: (res)=>{
        this.rider = res;
      },
      error: (err) =>{
        console.log(err);
      }
    })
  }

}
<div class="main-container">
    <h2>Nome: {{rider.firstName}}</h2>
    <h2>Numero di telefono: {{rider.phoneNumber}}</h2>
    <button class="btn btn-primary" (click)="authService.logout()">LogOut</button>
</div>
