import { Component, Input, OnInit } from '@angular/core';
import { AuthService } from 'src/app/services/auth.service';
import { UserService } from 'src/app/services/user.service';

@Component({
  selector: 'app-home-rider',
  templateUrl: './home-rider.component.html',
  styleUrls: ['./home-rider.component.scss']
})
export class HomeRiderComponent implements OnInit {

   user: any;
  rider: any;

  constructor(private userService: UserService){}

  ngOnInit(): void {
    this.user = localStorage.getItem('user');
    this.userService.getRider(this.user.rider_Id).subscribe({
      next: (res)=>{
        this.rider = res;
      },
      error: (err) =>{
        console.log(err);
      }
    })
  }

}
<div class="main-container">
    <h2>{{rider.firstName}}</h2>
    <h2>{{rider.phoneNumber}}</h2>
</div>
