import { UserService } from './../../services/user.service';
import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { AuthService } from 'src/app/services/auth.service';
//import { MessageService } from 'primeng/api';
import { Router } from '@angular/router';
import { pipe, take } from 'rxjs';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
  //providers: [MessageService],
})
export class HomeComponent {

  constructor(
    public authService: AuthService,
    //private messageService: MessageService,
    private userService: UserService,
    private router: Router){}

  loginForm = new FormGroup({
    username: new FormControl('', Validators.required),
    password: new FormControl('', Validators.required),
  }) 

  onSubmitLogin(){
    const user = {
      username: this.loginForm.value.username,
      password: this.loginForm.value.password,
    };
    this.userService.insertUser(this.loginForm.value).pipe(take(1))
    .subscribe({
      next: (res)=>{
        if(res){
          this.userService.datiUtente.next(user);
          //this.messageService.add({severity: 'success', summary: 'Success!', detail: 'Grazie per esserti registrato!', life: 2000});
          setTimeout(() => this.router.navigate(['home']), 2000);
        }
        else{
          //this.messageService.add({severity: 'error', summary: 'Error!', detail: 'Errore nella registrazione, riprova!', life: 2000});
        }
      },
      error: (err) =>{
        console.log(err);
        //this.messageService.add({severity: 'error', summary: 'Error!', detail: 'Errore nella registrazione, riprova!', life: 2000});
      }
    })
  }
}
<div class="main-container" *ngIf="!authService.isLogged()">
    <div class="login-column">
        <form [formGroup]="loginForm" (ngSubmit)="onSubmitLogin()">
            <h2>Se sei già registrato effettua il login per iniziare</h2>
            <div class="form-element">
                <label for="username">Username:</label>
                <input type="text"  id="username" name="username" class="form-control" formControlName="username">
                <!-- <p class="help is-danger" *ngIf="!loginForm.get('username')?.valid && loginForm.get('username').touched">Il campo username è obbligatorio</p>  -->
            </div>
            <div class="form-element">
                <label for="password">Password:</label>
                <input type="password"  id="password" name="password" class="form-control" formControlName="password">
                <!-- <p class="help is-danger" *ngIf="!loginForm.get('password')?.valid && loginForm.get('password').touched">Il campo password è obbligatorio</p>  -->
            </div>
            <button type="submit" class="btn btn-primary">Login</button>
        </form>
    </div>
    <div class="registration-column">

    </div>
</div>


<div *ngIf="authService.isLogged()">
    <h2>LALAALLALA</h2>
</div>
